    lastResults: SearchResults | null;
    derivedHelpers: DerivedHelper[];
    client: SearchClient;
    _queryId: number;
    _lastQueryIdReceived: number;
    _currentNbQueries: number;
    constructor(client: SearchClient, index: string, options: SearchParameters);
